import React, { useState } from "react";
import { Form, Button, Navbar } from "react-bootstrap";

function Login() {
  const [usernameOrEmail, setUsernameOrEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);

  function handleLogin(e) {
    e.preventDefault();
    const data = { username_or_email: usernameOrEmail, password };
    fetch("/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    })
      .then((res) => res.json())
      .then((result) => {
        if (result.status === "ok") {
          setLoggedIn(true);
          localStorage.setItem("token", result.data.token);
        } else {
          alert(result.message);
        }
      });
  }

  function handleLogout() {
    fetch("/users/logout", { method: "DELETE" })
      .then((res) => res.json())
      .then((result) => {
        if (result.status === "ok") {
          setLoggedIn(false);
          localStorage.removeItem("token");
        } else {
          alert(result.message);
        }
      });
  }

  return (
    <>
      <Navbar bg="light" variant="light">
        <Navbar.Brand href="#home">My Application</Navbar.Brand>
        <Navbar.Toggle />
        <Navbar.Collapse className="justify-content-end">
          {loggedIn ? (
            <Button variant="outline-secondary" onClick={handleLogout}>
              Logout
            </Button>
          ) : null}
        </Navbar.Collapse>
      </Navbar>

      <Form className="container mt-4" onSubmit={handleLogin}>
        <h2>Login</h2>
        <Form.Group controlId="formBasicUsernameOrEmail">
          <Form.Label>Username or Email address</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username or email"
            value={usernameOrEmail}
            onChange={(e) => setUsernameOrEmail(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Login
        </Button>
      </Form>
    </>
  );
}

export default Login;
