import React, { useState, useEffect } from 'react';

function CreateRecipe() {
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    fetch('/recipes/1')
      .then(response => response.json())
      .then(data => setRecipe(data))
      .catch(error => console.error(error));
  }, []);

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <h2>{recipe ? recipe.title : 'Loading...'}</h2>
          <p>Prep Time: {recipe ? recipe.prep_time : 'Loading...'} minutes</p>
          <p>Cook Time: {recipe ? recipe.cook_time : 'Loading...'} minutes</p>
          <p>Ingredients: {recipe ? recipe.ingredients : 'Loading...'}</p>
          <p>Instructions: {recipe ? recipe.instructions : 'Loading...'}</p>
        </div>
      </div>
    </div>
  );
}

export default CreateRecipe;
