import React from 'react';
import { Container } from 'react-bootstrap';

const Homepage = () => {
  return (
    <div style={{ 
        backgroundImage: `url(${process.env.PUBLIC_URL}/images/food.jpg)`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        height: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center'
      }}>
      <Container>
        <h1 style={{ color: 'white' }}>Welcome to our Recipe App!</h1>
        <p style={{ color: 'white' }}>Get inspired by our delicious recipes.</p>
      </Container>
    </div>
  );
}

export default Home;
