import React, { useState } from 'react';

function Login() {
  const [usernameOrEmail, setUsernameOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  function handleLogin(e) {
    e.preventDefault();
    fetch('/usr/login', {
      method: 'POST',
      body: JSON.stringify({
        usernameOrEmail: usernameOrEmail,
        password: password
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      if (response.ok) {
        setLoggedIn(true);
      } else {
        throw new Error('Login failed');
      }
    })
    .catch(error => {
      console.error('Login error:', error);
      setLoggedIn(false);
    });
  }

  function handleLogout() {
    fetch('/api/logout')
    .then(response => {
      if (response.ok) {
        setLoggedIn(false);
      } else {
        throw new Error('Logout failed');
      }
    })
    .catch(error => {
      console.error('Logout error:', error);
    });
  }

  if (loggedIn) {
    return (
      <div>
        <h2>You are logged in!</h2>
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  } else {
    return (
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label htmlFor="usernameOrEmail" className="form-label">Username or email</label>
          <input type="text" className="form-control" id="usernameOrEmail" value={usernameOrEmail} onChange={e => setUsernameOrEmail(e.target.value)} required />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <input type="password" className="form-control" id="password" value={password} onChange={e => setPassword(e.target.value)} required />
        </div>
        <button type="submit" className="btn btn-primary">Login</button>
      </form>
    );
  }
}

export default Login;
