import React, { useState } from 'react';

function Login() {
  const [usernameOrEmail, setUsernameOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username: usernameOrEmail, email: usernameOrEmail, password })
      });
      if (response.ok) {
        const data = await response.json();
        console.log(data);
        // handle successful login (e.g. save user data and token to state)
        setErrorMessage('');
        alert('Successfully logged in!');
        // redirect to home page (replace with your own logic)
        window.location.href = '/';
      } else {
        setErrorMessage('Invalid username/email or password');
      }
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  const handleLogout = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/users/logout');
      if (response.ok) {
        console.log('Successfully logged out!');
        // handle successful logout (e.g. clear user data and token from state)
        // redirect to login page (replace with your own logic)
        window.location.href = '/login';
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Log in to your account</h2>
      <form onSubmit={handleSubmit}>
        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
        <div className="form-group">
          <label htmlFor="usernameOrEmail">Username or Email:</label>
          <input
            type="text"
            className="form-control"
            id="usernameOrEmail"
            value={usernameOrEmail}
            onChange={(e) => setUsernameOrEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            className="form-control"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Log in</button>
      </form>
      <hr />
      <button type="button" className="btn btn-danger" onClick={handleLogout}>Log out</button>
    </div>
  );
}

export default Login;
