import { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';

const Login = () => {
  const [usernameOrEmail, setUsernameOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');

  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username_or_email: usernameOrEmail, password }),
      });
      if (response.ok) {
        setIsLoggedIn(true);
        setShowAlert(true);
        setAlertMessage('Login successful');
      } else {
        throw new Error('Invalid username/email or password');
      }
    } catch (error) {
      console.error(error);
      setShowAlert(true);
      setAlertMessage(error.message);
    }
  };

  const handleLogout = async () => {
    try {
      const response = await fetch('/users/logout', { method: 'POST' });
      if (response.ok) {
        setIsLoggedIn(false);
        setShowAlert(true);
        setAlertMessage('Logout successful');
      } else {
        throw new Error('Error logging out');
      }
    } catch (error) {
      console.error(error);
      setShowAlert(true);
      setAlertMessage(error.message);
    }
  };

  return (
    <div className="container">
      <h1>Login</h1>
      {showAlert && <Alert variant={isLoggedIn ? 'success' : 'danger'}>{alertMessage}</Alert>}
      {!isLoggedIn ? (
        <Form onSubmit={handleLogin}>
          <Form.Group controlId="usernameOrEmail">
            <Form.Label>Username or Email</Form.Label>
            <Form.Control type="text" placeholder="Enter username or email" value={usernameOrEmail} onChange={(e) => setUsernameOrEmail(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </Form.Group>

          <Button variant="primary" type="submit">
            Login
          </Button>
        </Form>
      ) : (
        <Button variant="danger" onClick={handleLogout}>
          Logout
        </Button>
      )}
    </div>
  );
};

export default Login;
