import React, { useState, useEffect } from "react";
import axios from "axios";

const Recipe = ({ recipe }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    title: recipe.title,
    ingredients: recipe.ingredients,
    instructions: recipe.instructions,
    prep_time: recipe.prep_time,
    category_id: recipe.category_id,
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleUpdate = () => {
    axios.put(`/recipes/${recipe.id}`, formData)
      .then(() => setIsEditing(false))
      .catch((error) => console.log(error));
  };

  const handleDelete = () => {
    axios.delete(`/recipes/${recipe.id}`)
      .then(() => console.log("Recipe deleted"))
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    setFormData({
      title: recipe.title,
      ingredients: recipe.ingredients,
      instructions: recipe.instructions,
      prep_time: recipe.prep_time,
      category_id: recipe.category_id,
    });
  }, [recipe]);

  return (
    <div className="well">
      {isEditing ? (
        <div>
          <input type="text" name="title" value={formData.title} onChange={handleInputChange} />
          <textarea name="ingredients" value={formData.ingredients} onChange={handleInputChange} />
          <textarea name="instructions" value={formData.instructions} onChange={handleInputChange} />
          <input type="number" name="prep_time" value={formData.prep_time} onChange={handleInputChange} />
          <input type="number" name="category_id" value={formData.category_id} onChange={handleInputChange} />
          <button onClick={handleUpdate}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      ) : (
        <div>
          <h3>{recipe.title}</h3>
          <p>{recipe.ingredients}</p>
          <p>{recipe.instructions}</p>
          <p>Prep Time: {recipe.prep_time} minutes</p>
          <p>Category: {recipe.category}</p>
          <button onClick={() => setIsEditing(true)}>Edit</button>
          <button onClick={handleDelete}>Delete</button>
        </div>
      )}
    </div>
  );
};

export default RecipeList;
